import{_ as i,c as s,a0 as a,j as t,n as o,o as n}from"./chunks/framework.BZ4Tck4q.js";const l="/hc-dev-training/assets/guillem_circular_small.CZY33YYq.png",d="/hc-dev-training/assets/viktor_circular_small.S5g39Ubq.png",h="/hc-dev-training/assets/bear_circular_small.BlHMof5G.png",c="_button_1f9z5_2",u={button:c},q=JSON.parse('{"title":"","description":"","frontmatter":{"layout":"home","hero":{"name":"Holochain Developer Training","text":"","tagline":"February 3 – 14","actions":[{"theme":"brand","text":"Apply Now","link":"https://forms.gle/QPZ8yR4sYZvJtWH57"}]},"features":[{"title":"Lectures","details":"Guillem will be walking you through the different elements of the course and using live visualizations of how Holochain works."},{"title":"Excercises","details":"There are exercises built into the interactive learning materials to make sure that all of the concepts land for you as a student."},{"title":"Mini-hackathon","details":"At the end of the course you will have time to build a very small app in order to get familiar with the actual process of building."}]},"headers":[],"relativePath":"index.md","filePath":"index.md"}'),p={name:"index.md"},m={href:"https://forms.gle/QPZ8yR4sYZvJtWH57"},f={href:"https://forms.gle/QPZ8yR4sYZvJtWH57"};function g(r,e,y,w,x,v){return n(),s("div",null,[e[0]||(e[0]=a('<h2 id="outcome" tabindex="-1">Outcome <a class="header-anchor" href="#outcome" aria-label="Permalink to &quot;Outcome&quot;">​</a></h2><p>Participants will walk away from the course with the ability to make simple Holochain apps.</p><h2 id="course-overview" tabindex="-1">Course Overview <a class="header-anchor" href="#course-overview" aria-label="Permalink to &quot;Course Overview&quot;">​</a></h2><p>This training will run over the course of two weeks beginning on Monday the 3rd of February. Each day has around 2 hours of theory on zoom and then there are exercises to complete before the next session (expect around 2h for this depending on person and skill).</p><p>The live sessions will be held at: 8 am PST / 11 am EST / 6pm CEST.</p><p>After we have gone through all the theory, you get to practice it through implementing a small app (we provide a few different designs to choose from) in the hackathon days.</p><p>During the two weeks after the course we will give ongoing asynchronous support for students that are building things and at the end of these two weeks we also offer a live session where we answer any questions or considerations that might have come up in building.</p><h2 id="details" tabindex="-1">Details <a class="header-anchor" href="#details" aria-label="Permalink to &quot;Details&quot;">​</a></h2><div class="headerless"><table tabindex="0"><thead><tr><th></th><th style="text-align:center;"></th></tr></thead><tbody><tr><td>Dates:</td><td style="text-align:center;"><strong>February 3-14</strong></td></tr><tr><td>Location:</td><td style="text-align:center;"><strong>Online classroom (Zoom)</strong></td></tr><tr><td>Group size:</td><td style="text-align:center;"><strong>12</strong></td></tr><tr><td>Price:</td><td style="text-align:center;"><strong>$2000</strong></td></tr></tbody></table></div>',9)),t("p",null,[t("a",m,[t("button",{style:{"margin-top":"20px","margin-bottom":"20px"},class:o(r.$style.button)},"Apply Now",2)])]),e[1]||(e[1]=a('<p>We recognize that the tech space is inequitable, so we have some bursaries available for applicants from systemically underrepresented groups. If this is you, please reach out to us at <a href="mailto:hello@darksoil.studio" target="_blank" rel="noreferrer">hello@darksoil.studio</a>.</p><h2 id="prerequisite-basic-rust" tabindex="-1">Prerequisite: Basic Rust <a class="header-anchor" href="#prerequisite-basic-rust" aria-label="Permalink to &quot;Prerequisite: Basic Rust&quot;">​</a></h2><p>While much of a Holochain app is coded in Javascript using your preferred front end frameworks, Rust is required for the core Holochain functionality and integrity, so we require some Rust experience as a prerequisite for this training.</p><h2 id="schedule" tabindex="-1">Schedule <a class="header-anchor" href="#schedule" aria-label="Permalink to &quot;Schedule&quot;">​</a></h2><table tabindex="0"><thead><tr><th><strong>Date</strong></th><th style="text-align:center;"><strong>Topic</strong></th></tr></thead><tbody><tr><td>February 3</td><td style="text-align:center;">How Does Holochain Work?</td></tr><tr><td>February 4</td><td style="text-align:center;">Building Basic Holochain Apps</td></tr><tr><td>February 5</td><td style="text-align:center;">Queries and Capability Tokens</td></tr><tr><td>February 6</td><td style="text-align:center;">Validation</td></tr><tr><td>February 7</td><td style="text-align:center;">Connecting from a UI</td></tr><tr><td></td><td style="text-align:center;"></td></tr><tr><td>February 10</td><td style="text-align:center;">DNA - Modifiers and Clones</td></tr><tr><td>February 11</td><td style="text-align:center;">Ecosystem Tooling &amp; Hackathon Start</td></tr><tr><td>February 12</td><td style="text-align:center;">Hackathon Support Session</td></tr><tr><td>February 13</td><td style="text-align:center;">Hackathon Support Session</td></tr><tr><td>February 14</td><td style="text-align:center;">Hackathon End &amp; Closing</td></tr><tr><td></td><td style="text-align:center;"></td></tr><tr><td>February 28:</td><td style="text-align:center;">Progress Updates (Optional)</td></tr></tbody></table><h2 id="apply-for-the-training" tabindex="-1">Apply for the training <a class="header-anchor" href="#apply-for-the-training" aria-label="Permalink to &quot;Apply for the training&quot;">​</a></h2><p>We are limiting this course to 12 participants for a more intimate learning environment with lots of individual support.</p><p>Applications are accepted on a first come, first serve basis. Once the course is full, we will put applicants on the wait list for the next training.</p>',8)),t("p",null,[t("a",f,[t("button",{class:o(r.$style.button)},"Apply Now",2)])]),e[2]||(e[2]=a('<h2 id="meet-the-team" tabindex="-1">Meet the team <a class="header-anchor" href="#meet-the-team" aria-label="Permalink to &quot;Meet the team&quot;">​</a></h2><div style="display:flex;flex-direction:row;flex-wrap:wrap;gap:20px;"><div style="flex:1;display:flex;flex-direction:column;align-items:center;"><p><img src="'+l+'" alt="Guillem Cordoba"></p><p><strong>Guillem Cordoba</strong> has been a key contributor to both the Holochain protocol and the ecosystem as a whole, building apps, core tooling and developer education programs for 5+ years.</p><p>He is one of the world&#39;s most experienced and knowledgeable Holochain developers and an outstanding teacher.</p></div><div style="flex:1;display:flex;flex-direction:column;align-items:center;"><p><img src="'+d+'" alt="Viktor Zaunders"></p><p><strong>Viktor Zaunders</strong> is an educational organizer who is passionate about learning and creating spaces where people feel comfortable.</p><p>He will be doing the support around the course.</p></div><div style="flex:1;display:flex;flex-direction:column;align-items:center;"><p><img src="'+h+'" alt="Eric Bear"></p><p><strong>Eric Bear</strong> is a community organizer with more than a decade of experience in creating learning environments.</p><p>He will be supporting the structure and experience.</p></div></div><div class="FAQ"><h2 id="faq" tabindex="-1">FAQ <a class="header-anchor" href="#faq" aria-label="Permalink to &quot;FAQ&quot;">​</a></h2><h4 id="are-the-sessions-recorded" tabindex="-1">Are the sessions recorded? <a class="header-anchor" href="#are-the-sessions-recorded" aria-label="Permalink to &quot;Are the sessions recorded?&quot;">​</a></h4><p>Yes, sessions will be recorded and available to everyone for two months after the course.</p><h4 id="will-the-materials-for-the-course-be-available-for-some-time-after-the-course-ends" tabindex="-1">Will the materials for the course be available for some time after the course ends? <a class="header-anchor" href="#will-the-materials-for-the-course-be-available-for-some-time-after-the-course-ends" aria-label="Permalink to &quot;Will the materials for the course be available for some time after the course ends?&quot;">​</a></h4><p>Yes, they will be available for two months after the course ends.</p><h4 id="what-if-someone-is-rude-mean-or-worse" tabindex="-1">What if someone is rude, mean, or worse? <a class="header-anchor" href="#what-if-someone-is-rude-mean-or-worse" aria-label="Permalink to &quot;What if someone is rude, mean, or worse?&quot;">​</a></h4><p>We require participants to read and agree to our code of conduct, if anyone’s behavior violates the code of conduct we will address it immediately, with anything from discussion to removal from the course.</p><h4 id="what-s-the-deal-with-the-february-28th-session" tabindex="-1">What’s the deal with the February 28th session? <a class="header-anchor" href="#what-s-the-deal-with-the-february-28th-session" aria-label="Permalink to &quot;What’s the deal with the February 28th session?&quot;">​</a></h4><p>We will hold a session for participants to share what they’ve been building, ask questions, get support, and offer feedback.</p><h4 id="i-can-t-make-it-to-this-one-will-there-be-more-when" tabindex="-1">I can’t make it to this one, will there be more? When? <a class="header-anchor" href="#i-can-t-make-it-to-this-one-will-there-be-more-when" aria-label="Permalink to &quot;I can’t make it to this one, will there be more? When?&quot;">​</a></h4><p>We are planning to run another, but we don’t know when. Apply now to reserve your place for the next course. In “Notes” section of the application, say you’re applying for the next course.</p><h4 id="what-communication-channel-will-we-use" tabindex="-1">What communication channel will we use? <a class="header-anchor" href="#what-communication-channel-will-we-use" aria-label="Permalink to &quot;What communication channel will we use?&quot;">​</a></h4><p>We’ll use our discord server and invite participants to a private channel.</p><h4 id="more-questions" tabindex="-1">More questions? <a class="header-anchor" href="#more-questions" aria-label="Permalink to &quot;More questions?&quot;">​</a></h4><p>Please reach out to us at <a href="mailto:hello@darksoil.studio" target="_blank" rel="noreferrer">hello@darksoil.studio</a>.</p></div>',3))])}const b={$style:u},P=i(p,[["render",g],["__cssModules",b]]);export{q as __pageData,P as default};
